mapper.update:
UPDATE t_inventory
SET item = #{itemUuid}
WHERE uuid = #{uuid}
  AND slot = #{slot}
  AND i = #{index}

mapper.select:
SELECT count(*) > 0
FROM t_inventory
WHERE uuid = #{uuid}
  AND slot = #{slot}
  AND i = #{index}

SELECT item
FROM t_inventory
WHERE uuid = #{uuid}
  AND slot = #{slot}
  AND i = #{index}
